
function KeyboardManager(){this.listening=true;this.keys=[];var a=this;window.addEvent("keydown",function(b){a.keydown.call(a,b)});window.addEvent("keyup",function(b){a.keyup.call(a,b)})}KeyboardManager.UP=90;KeyboardManager.RIGHT=68;KeyboardManager.DOWN=83;KeyboardManager.LEFT=81;KeyboardManager.ENTER=13;KeyboardManager.SPACE=32;KeyboardManager.ESCAPE=27;KeyboardManager.prototype={keydown:function(a){if(this.listening){if(!this.isPressed(a.keyCode)){this.keys.push(a.keyCode)}}},keyup:function(a){if(this.listening){this.keys.out(a.keyCode)}},isPressed:function(a){return(this.keys.indexOf(a)===-1)?false:true},toggleListen:function(){this.listening=!this.listening}};